import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import Recomended from '../components/Recomended';
import blackHeart from '../images/blackHeartIcon.svg';
import whiteHeart from '../images/whiteHeartIcon.svg';

function FoodDetails() {
  const history = useHistory();
  fetch('https://www.thecocktaildb.com/api/json/v1/1/search.php?s=');
  fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=');
  const [response, setResponse] = useState([]);
  const requestWithId = () => {
    const href = document.location.href.split('/');
    const idFood = href[href.length - 1];
    fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${idFood}`)
      .then((x) => x.json()).then((y) => setResponse(y));
  };
  useEffect(() => {
    requestWithId();
  }, []);
  function renderIngredients() {
    const food = response.meals[0];
    console.log(food);
    const foodIngredient = Object.values(Object
      .fromEntries(Object
        .entries(food)
        .filter(([key]) => key.includes('strIngredient'))))
      .filter((x) => x !== '');
    const foodMeasure = Object.values(Object
      .fromEntries(Object
        .entries(food)
        .filter(([key]) => key.includes('strMeasure'))))
      .filter((x) => x !== '');
    const ing = '-ingredient-name-and-measure';
    return foodIngredient
      .map((x, index) => (
        <div key={ `${index}-food-ingrediet` }>
          <li key={ x } data-testid={ `${index}${ing}` }>{x}</li>
          <li key={ x } data-testid={ `${index}${ing}` }>{foodMeasure[index]}</li>
        </div>
      ));
  }
  const renderDetails = () => {
    const food = response.meals[0];
    return (
      <div>
        <img
          src={ food.strMealThumb }
          alt={ food.meal }
          width="100px"
          height="100px"
          data-testid="recipe-photo"
        />
        <h1 data-testid="recipe-title">{ food.strMeal }</h1>
        <button
          type="button"
          data-testid="share-btn"
          className="share-btn"
          onClick={ () => {
            navigator.clipboard.writeText(`http://localhost:3000${history.location.pathname}`);
            const btn = document.querySelector('.share-btn');
            btn.innerHTML = 'Link copied!';
          } }
        >
          share
        </button>
        <button
          type="button"
          data-testid="favorite-btn"
          src={ srcImg }
          onClick={ () => {
            const favObject = [{
              id: food.idFood,
              type: 'bebida',
              nationality: '',
              category: '',
              alcoholicOrNot: '',
              name: '',
              image: '',
            }];
            localStorage.setItem('favoriteRecipes', JSON.stringify(favObject));
            if (srcImg === blackHeart) {
              setSrcImg(whiteHeart);
            } else {
              setSrcImg(blackHeart);
            }
            console.log(JSON.parse(localStorage.getItem('favoriteRecipes')));
          } }
        >
          <img src={ srcImg } alt="fav" />
        </button>
        <p data-testid="recipe-category">{ food.strCategory }</p>
        <ul>
          {renderIngredients()}
        </ul>
        <p data-testid="instructions">{ food.strInstructions }</p>
        <video width="320" height="240" data-testid="video" controls>
          <track kind="captions" />
          <source src={ food.strSource } type="video/mp4" />
        </video>
        <Recomended />
        <button
          data-testid="start-recipe-btn"
          type="button"
          className="start-button"
        >
          iniciar
        </button>
      </div>
    );
  };
  return (
    <div className="details">
      {
        response.length === 0 ? null : renderDetails()
      }
    </div>
  );
}

export default FoodDetails;
