import React, { useEffect, useState, useContext } from 'react';
import { useHistory } from 'react-router-dom';
import Recomended from '../components/Recomended';
import recipesContext from '../context/recipesContext';
import VerifyFav from '../helpers/verifyFav';

function DrinkDetails() {
  const history = useHistory();
  const {
    srcImg,
  } = useContext(recipesContext);
  VerifyFav();
  fetch('https://www.thecocktaildb.com/api/json/v1/1/search.php?s=');
  fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=');
  const [res, setResponse] = useState([]);
  const requestWithId = () => {
    const href = document.location.href.split('/');
    const idFood = href[href.length - 1];
    fetch(`https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${idFood}`)
      .then((x) => x.json()).then((y) => {
        setResponse(y);
      }).then(() => {
        verifyFav(idFood);
      });
  };
  useEffect(() => {
    requestWithId();
  }, []);
  function renderIngredients() {
    const drink = res.drinks[0];
    console.log(drink);
    const foodIngredient = Object.values(Object
      .fromEntries(Object
        .entries(drink)
        .filter(([key]) => key.includes('strIngredient'))))
      .filter((x) => x !== null);
    const foodMeasure = Object.values(Object
      .fromEntries(Object
        .entries(drink)
        .filter(([key]) => key.includes('strMeasure'))))
      .filter((x) => x !== null);
    const ing = '-ingredient-name-and-measure';
    return foodIngredient
      .map((x, index) => (
        <div key={ `${index}-food-ingrediet` }>
          <li key={ x } data-testid={ `${index}${ing}` }>{x}</li>
          <li key={ x } data-testid={ `${index}${ing}` }>{foodMeasure[index]}</li>
        </div>
      ));
  }
  const renderDetails = () => {
    const drink = res.drinks[0];
    return (
      <div>
        <img
          src={ drink.strDrinkThumb }
          alt={ drink.drink }
          width="100px"
          height="100px"
          data-testid="recipe-photo"
        />
        <h1 data-testid="recipe-title">{ drink.strDrink }</h1>
        <button
          type="button"
          data-testid="share-btn"
          className="share-btn"
          onClick={ () => {
            navigator.clipboard.writeText(`http://localhost:3000${history.location.pathname}`);
            const btn = document.querySelector('.share-btn');
            btn.innerHTML = 'Link copied!';
          } }
        >
          share
        </button>
        <button
          type="button"
          data-testid="favorite-btn"
          src={ srcImg }
          onClick={ () => {
            if (localStorage.getItem('favoriteRecipes') !== null) {
              const oldFavs = JSON.parse(localStorage.getItem('favoriteRecipes'));
              const favObject = [{
                id: drink.idDrink,
                type: 'bebida',
                nationality: '',
                category: '',
                alcoholicOrNot: '',
                name: '',
                image: '',
              }, ...oldFavs];
              localStorage.setItem('favoriteRecipes', JSON.stringify(favObject));
            } else {
              const favObject = [{
                id: drink.idDrink,
                type: 'bebida',
                nationality: '',
                category: '',
                alcoholicOrNot: '',
                name: '',
                image: '',
              }];
              localStorage.setItem('favoriteRecipes', JSON.stringify(favObject));
            }
            verifyFav();
          } }
        >
          <img src={ srcImg } alt="fav" />
        </button>
        <p data-testid="recipe-category">{ drink.strAlcoholic }</p>
        <ul>
          {renderIngredients()}
        </ul>
        <p data-testid="instructions">{ drink.strInstructions }</p>
        <video width="320" height="240" data-testid="video" controls>
          <track kind="captions" />
          <source src={ drink.strSource } type="video/mp4" />
        </video>
        <Recomended />
        <button
          data-testid="start-recipe-btn"
          type="button"
          className="start-button"
        >
          iniciar
        </button>
      </div>
    );
  };
  return (
    <div className="details">
      {
        res.length === 0 ? null : renderDetails()
      }
    </div>
  );
}

export default DrinkDetails;
