import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { useHistory } from 'react-router-dom';
import saveRecipe from '../helpers/saveRecipe';
import Ingredients from './Ingredients';
import Recomended from './Recomended';
import blackHeart from '../images/blackHeartIcon.svg';
import whiteHeart from '../images/whiteHeartIcon.svg';

function RenderDetailsFood({ res }) {
  const [srcImg, setSrcImg] = useState(whiteHeart);
  function verifyFav() {
    const href = document.location.href.split('/');
    const id = href[href.length - 2];
    console.log(id);
    let favs = JSON.parse(localStorage.getItem('favoriteRecipes'));
    if (favs !== null) {
      favs = Object.values(favs);
      if (favs.some((x) => x.id === id)) {
        setSrcImg(blackHeart);
      }
    }
  }
  useEffect(() => {
    verifyFav();
  }, []);
  const history = useHistory();
  const {
    strArea,
    idMeal,
    strMealThumb,
    strMeal,
    strCategory,
    strInstructions,
    strSource,
  } = res;
  console.log(res);
  return (
    <div>
      <img
        src={ strMealThumb }
        alt="meal-thumb"
        width="100px"
        height="100px"
        data-testid="recipe-photo"
      />
      <h1 data-testid="recipe-title">{ strMeal }</h1>
      <button
        type="button"
        data-testid="share-btn"
        className="share-btn"
        onClick={ () => {
          navigator.clipboard.writeText(`http://localhost:3000${history.location.pathname}`);
          const btn = document.querySelector('.share-btn');
          btn.innerHTML = 'Link copied!';
        } }
      >
        share
      </button>
      <button
        type="button"
        data-testid="favorite-btn"
        src={ srcImg }
        onClick={ () => {
          const favObject = [{
            id: idMeal,
            type: '',
            nationality: strArea,
            category: strCategory,
            alcoholicOrNot: '',
            name: strMeal,
            image: strMealThumb,
          }];
          saveRecipe(favObject);
          if (srcImg === blackHeart) {
            setSrcImg(whiteHeart);
          } else {
            setSrcImg(blackHeart);
          }
        } }
      >
        <img src={ srcImg } alt="fav" />
      </button>
      <p data-testid="recipe-category">{ strCategory }</p>
      <ul>
        <Ingredients element={ res } />
      </ul>
      <p data-testid="instructions">{ strInstructions }</p>
      <video width="320" height="240" data-testid="video" controls>
        <track kind="captions" />
        <source src={ strSource } type="video/mp4" />
      </video>
      <Recomended />
      <button
        data-testid="start-recipe-btn"
        type="button"
        className="start-button"
        onClick={ () => {
          history.push(`/foods/${idMeal}/in-progress`);
        } }
      >
        iniciar
      </button>
    </div>
  );
}

export default RenderDetailsFood;

RenderDetailsFood.propTypes = {
  res: PropTypes.shape({
    strArea: PropTypes.string.isRequired,
    strMealThumb: PropTypes.string.isRequired,
    strMeal: PropTypes.string.isRequired,
    strCategory: PropTypes.string.isRequired,
    strInstructions: PropTypes.string.isRequired,
    strSource: PropTypes.string.isRequired,
    idMeal: PropTypes.string.isRequired,
  }).isRequired,
};
