import React, { useContext, useEffect } from 'react';
import recipesContext from '../context/recipesContext';

function ElementCard() {
  const {
    apiSearch,
  } = useContext(recipesContext);

  useEffect(() => {
    console.log(apiSearch);
  }, [apiSearch]);

  const renderCards = () => {
    apiSearch.map((card, index) => (
      <div data-testid={ `${index}-recipe-card` } key={ index }>{ card.srtMeal }</div>
    ))
  }

  return (
    <div className="elements-cards">{apiSearch.length > 0 ? { renderCards } : null}</div>
  );
}

export default ElementCard;
