import React from 'react';
import PropTypes from 'prop-types';
import { useHistory } from 'react-router-dom';

function Ingredients({ element }) {
  const retorno = JSON.parse(localStorage.getItem('inProgressRecipes'));
  if (retorno) {
    const obj = {
      cocktails: {
      },
      meals: {
      },
    };
    console.log(JSON.stringify(obj));
    localStorage.setItem('inProgressRecipes', JSON.stringify(obj));
  }
  const verifyCheck = (value) => {
    const re = JSON.parse(localStorage.getItem('inProgressRecipes'));
    if (re === undefined || re === null) {
      return false;
    }
    let { cocktails, meals } = JSON.parse(localStorage.getItem('inProgressRecipes'));
    cocktails = Object.values(cocktails);
    meals = Object.values(meals);
    if (meals.some((x) => x === value) || cocktails.some((x) => x === value)) {
      return true;
    }
    return false;
  };
  const history = useHistory();
  const handleCLick = ({ target }) => {
    target.checked = true;
    const { value } = target;
    const { cocktails, meals } = JSON.parse(localStorage.getItem('inProgressRecipes'));
    let array = [value];
    if (cocktails[element.idMeal] !== undefined) {
      array = cocktails[element.idMeal];
      array.push(value);
    }
    const newObject = {
      cocktails: {
        ...cocktails,
        [element.idMeal]: array,
      },
      meals: {
        ...meals,
      },
    };
    localStorage.setItem('inProgressRecipes', JSON.stringify(newObject));
  };
  const foodIngredient = Object.values(Object
    .fromEntries(Object
      .entries(element)
      .filter(([key]) => key.includes('strIngredient'))))
    .filter((x) => x !== null && x !== '');
  const foodMeasure = Object.values(Object
    .fromEntries(Object
      .entries(element)
      .filter(([key]) => key.includes('strMeasure'))))
    .filter((x) => x !== null && x !== '');
  const ing = '-ingredient-name-and-measure';
  if (history.location.pathname.includes('/in-progress')) {
    return foodIngredient
      .map((x, index) => {
        let check = false;
        if (verifyCheck(x)) {
          check = true;
        }
        return (
          <div
            key={ `${index}-food-ingrediet` }
            data-testid={ `${index}-ingredient-step` }
          >
            <input
              type="checkbox"
              key={ `${index}-food` }
              name={ `${index}-checkbox` }
              onClick={ handleCLick }
              checked={ check }
              value={ x }
            />
            <label htmlFor={ `${index}-checkbox` }>{ x }</label>
          </div>
        );
      });
  }
  return foodIngredient
    .map((x, index) => (
      <div key={ `${index}-food-ingrediet` }>
        <li key={ `${index}-food` } data-testid={ `${index}${ing}` }>{x}</li>
        <li
          key={ `${index}-` }
          data-testid={ `${index}${ing}` }
        >
          {foodMeasure[index]}
        </li>
      </div>
    ));
}

export default Ingredients;

Ingredients.propTypes = {
  element: PropTypes.shape({
    element: PropTypes.string.isRequired,
  }).isRequired,
};
