import React, { useState } from 'react';
import PropTypes from 'prop-types';
import shareIcon from '../images/shareIcon.svg';

function RenderRecipes() {
  let dones = JSON.parse(localStorage.getItem('doneRecipes'));
  dones = dones.map((x) => {
    if (x.type === 'food') {
      x.category = `${x.nationality} - ${x.category}`;
    } else {
      x.category = x.alcoholicOrNot;
    }
    return x;
  });
  const [renderList, setRenderList] = useState(dones);
  return (
    <div>
      <button
        type="button"
        data-testid="filter-by-all-btn"
        onClick={ () => {
          setRenderList(dones);
        } }
      >
        All
      </button>
      <button
        type="button"
        data-testid="filter-by-food-btn"
        onClick={ () => {
          setRenderList(dones.filter((x) => x.type === 'food'));
        } }
      >
        Food
      </button>
      <button
        type="button"
        data-testid="filter-by-drink-btn"
        onClick={ () => {
          setRenderList(dones.filter((x) => x.type === 'drink'));
        } }
      >
        Drinks
      </button>
      {
        renderList.map((x, index) => (
          <div key={ `${index}-done` }>
            <img
              src={ x.image }
              alt="img"
              data-testid={ `${index}-horizontal-image` }
              width="150px"
            />
            <p data-testid={ `${index}-horizontal-top-text` }>
              {
                x.category
              }
            </p>
            <p data-testid={ `${index}-horizontal-name` }>
              {
                x.name
              }
            </p>
            <p data-testid={ `${index}-horizontal-done-date` }>
              {
                x.doneDate
              }
            </p>
            <button
              data-testid={ `${index}-horizontal-share-btn` }
              type="button"
              className="share-btn-done"
              src={ shareIcon }
              onClick={ () => {
                navigator.clipboard.writeText(`http://localhost:3000/${x.type}s/${x.id}`);
                const btn = document.querySelector('.share-btn-done');
                btn.innerHTML = 'Link copied!';
              } }
            >
              <img alt="share" src={ shareIcon } />
            </button>
            {
              x.tags.map((y, ind) => (
                <p key={ `${ind}-tags` } data-testid={ `${index}-${y}-horizontal-tag` }>
                  {
                    y
                  }
                </p>
              ))
            }
          </div>
        ))
      }
    </div>
  );
}
export default RenderRecipes;

RenderRecipes.prototype = {
  recipe: PropTypes.shape({
    src: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    cat: PropTypes.string.isRequired,
  }).isRequired,
};
