import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import Ingredients from '../components/Ingredients';
import Recomended from '../components/Recomended';
import saveRecipe from '../helpers/saveRecipe';
import blackHeart from '../images/blackHeartIcon.svg';
import whiteHeart from '../images/whiteHeartIcon.svg';

function FoodDetails() {
  const history = useHistory();
  fetch('https://www.thecocktaildb.com/api/json/v1/1/search.php?s=');
  fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=');
  const [response, setResponse] = useState([]);
  const [area, setArea] = useState('');
  const [srcImg, setSrcImg] = useState(whiteHeart);
  function verifyFav(id) {
    console.log(id);
    let favs = JSON.parse(localStorage.getItem('favoriteRecipes'));
    if (favs !== null) {
      favs = Object.values(favs);
      if (favs.some((x) => x.id === id)) {
        setSrcImg(blackHeart);
      }
      console.log('lint');
    }
  }

  const requestWithId = () => {
    const href = document.location.href.split('/');
    const idFood = href[href.length - 1];
    fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${idFood}`)
      .then((x) => x.json()).then((y) => setResponse(y)).then(() => {
        fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=Arrabiata')
          .then((x) => x.json()).then((y) => setArea(y.meals[0].strArea));
        verifyFav(idFood);
      });
  };
  useEffect(() => {
    requestWithId();
  }, []);
  const renderDetails = () => {
    const food = response.meals[0];
    return (
      <div>
        <img
          src={ food.strMealThumb }
          alt={ food.meal }
          width="100px"
          height="100px"
          data-testid="recipe-photo"
        />
        <h1 data-testid="recipe-title">{ food.strMeal }</h1>
        <button
          type="button"
          data-testid="share-btn"
          className="share-btn"
          onClick={ () => {
            navigator.clipboard.writeText(`http://localhost:3000${history.location.pathname}`);
            const btn = document.querySelector('.share-btn');
            btn.innerHTML = 'Link copied!';
          } }
        >
          share
        </button>
        <button
          type="button"
          data-testid="favorite-btn"
          src={ srcImg }
          onClick={ () => {
            const favObject = [{
              id: food.idMeal,
              type: 'bebida',
              nationality: area,
              category: '',
              alcoholicOrNot: '',
              name: '',
              image: '',
            }];
            saveRecipe(favObject);
            if (srcImg === blackHeart) {
              setSrcImg(whiteHeart);
            } else {
              setSrcImg(blackHeart);
            }
          } }
        >
          <img src={ srcImg } alt="fav" />
        </button>
        <p data-testid="recipe-category">{ food.strCategory }</p>
        <ul>
          <Ingredients element={ food } />
        </ul>
        <p data-testid="instructions">{ food.strInstructions }</p>
        <video width="320" height="240" data-testid="video" controls>
          <track kind="captions" />
          <source src={ food.strSource } type="video/mp4" />
        </video>
        <Recomended />
        <button
          data-testid="start-recipe-btn"
          type="button"
          className="start-button"
          onClick={ () => {
            history.push(`/foods/${food.idMeal}/in-progress`);
          } }
        >
          iniciar
        </button>
      </div>
    );
  };
  return (
    <div className="details">
      {
        response.length === 0 ? null : renderDetails()
      }
    </div>
  );
}

export default FoodDetails;
