import React, { useContext, useState } from 'react';
import recipesContext from '../context/recipesContext';

function SearchBar() {
  const [valueRadio, setValueRadio] = useState('');
  const {
    setApiSearch,
  } = useContext(recipesContext);
  const requestApi = () => {
    switch (valueRadio) {
    case 'ingredient':
      fetch(`https://www.themealdb.com/api/json/v1/1/filter.php?i=${ingrediente}`)
        .then((x) => x.json()).then((y) => setApiSearch(y));
      break;
    default:
      console.log('default');
    }
  };
  const handleClick = ({ target: { value } }) => {
    const radios = document.querySelectorAll('.radiosSearch');
    for (let i = 0; i < radios.length; i += 1) {
      if (radios[i].value !== value) {
        radios[i].checked = false;
      }
    }
    setValueRadio(value);
  };
  return (
    <div>
      <input type="text" data-testid="search-input" placeholder="search itens" />
      <p>Select a type of search</p>
      <div className="radios">
        <label htmlFor="ing">
          <input
            className="radiosSearch"
            type="radio"
            data-testid="ingredient-search-radio"
            id="ing"
            value="ingredient"
            onClick={ handleClick }
          />
          Ingredient
        </label>
        <label htmlFor="name">
          <input
            className="radiosSearch"
            type="radio"
            data-testid="name-search-radio"
            id="name"
            value="name"
            onClick={ handleClick }
          />
          name
        </label>
        <label htmlFor="first">
          <input
            className="radiosSearch"
            type="radio"
            data-testid="first-letter-search-radio"
            id="first"
            value="first"
            onClick={ handleClick }
          />
          First Letter
        </label>
        <button
          type="button"
          data-testid="exec-search-btn"
          onClick={ requestApi }
        >
          Search
        </button>
      </div>

    </div>
  );
}

export default SearchBar;
