import React, { useContext, useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import recipesContext from '../context/recipesContext';

function ElementCard() {
  const [catFoods, setCatFoods] = useState([]);
  const [catDrinks, setCatDrinks] = useState([]);
  const [loading, setLoading] = useState(true);

  const requestCat = () => {
    fetch('https://www.themealdb.com/api/json/v1/1/list.php?c=list')
      .then((x) => x.json()).then((y) => {
        console.log(y.meals);
        y.meals.length = 5;
        setLoading(false);
        setCatFoods(y.meals);
      });
    fetch('https://www.thecocktaildb.com/api/json/v1/1/list.php?c=list')
      .then((x) => x.json()).then((y) => {
        console.log(y.drinks);
        y.drinks.length = 5;
        setLoading(false);
        setCatDrinks(y.drinks);
      });
  };
  const {
    apiSearch,
    setApiSearch,
  } = useContext(recipesContext);
  const history = useHistory();
  if (Object.keys(apiSearch).length === 0) {
    if (history.location.pathname === '/foods') {
      fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=')
        .then((x) => x.json()).then((y) => {
          y.meals.length = 12;
          setApiSearch(y);
        });
    } else if (history.location.pathname === '/drinks') {
      fetch('https://www.thecocktaildb.com/api/json/v1/1/search.php?s=')
        .then((x) => x.json()).then((y) => {
          y.drinks.length = 12;
          console.log(y);
          setApiSearch(y);
        });
    }
  }

  useEffect(() => {
    requestCat();
  }, []);

  const renderCards = () => {
    if (history.location.pathname === '/foods') {
      return apiSearch.meals.map((card, index) => (
        <div
          data-testid={ `${index}-recipe-card` }
          key={ index }
          className="cards"
        >
          <h1 data-testid={ `${index}-card-name` }>{ card.strMeal }</h1>
          <img
            alt="food"
            src={ card.strMealThumb }
            width="100px"
            height="100px"
            data-testid={ `${index}-card-img` }
          />
        </div>
      ));
    } if (history.location.pathname === '/drinks') {
      return apiSearch.drinks.map((card, index) => (
        <div
          data-testid={ `${index}-recipe-card` }
          key={ index }
          className="cards"
        >
          <h1 data-testid={ `${index}-card-name` }>{ card.strDrink }</h1>
          <img
            alt="food"
            src={ card.strDrinkThumb }
            width="100px"
            height="100px"
            data-testid={ `${index}-card-img` }
          />
        </div>
      ));
    }
  };

  const handleCLick = ({ target: value }) => {
    setApiSearch([]);
    if (history.location.pathname === '/drinks') {
      fetch(`www.thecocktaildb.com/api/json/v1/1/filter.php?c=${value}`)
        .then((result) => result.json()).then((p) => {
          p.drinks.length = 12;
          setApiSearch(y);
        });
    } else if (history.location.pathname === '/foods') {
      fetch(`www.themealdb.com/api/json/v1/1/filter.php?c=${value}`)
        .then((result) => result.json()).then((y) => {
          y.meals.length = 12;
          setApiSearch(y);
        });
    }
  };

  const renderCategorias = () => {
    if (history.location.pathname === '/foods') {
      return (
        catFoods.map((x, index) => (
          <button
            type="button"
            value={ x.strCategory }
            key={ `cat${index}` }
            className="cat-card"
            data-testid={ `${x.strCategory}-category-filter` }
            onClick={ handleCLick }
          >
            {
              x.strCategory
            }
          </button>))
      );
    }
    if (history.location.pathname === '/drinks') {
      return (
        catDrinks.map((y, index) => (
          <button
            type="button"
            key={ `cat${index}` }
            value={ y.strCategory }
            className="cat-card"
            data-testid={ `${y.strCategory}-category-filter` }
            onClick={ handleCLick }
          >
            {
              y.strCategory
            }
          </button>))
      );
    }
  };

  return (
    <div
      className="elements-cards"
    >
      <div className="category">
        { loading ? null : renderCategorias() }
      </div>
      {Object.keys(apiSearch).length > 0 ? <div>{ renderCards() }</div> : null}
    </div>
  );
}

export default ElementCard;
