import React, { useContext, useState, useEffect } from 'react';
import ElementCardFoods from '../components/ElementCardFoods';
import Header from '../components/Header';
import recipesContext from '../context/recipesContext';

function Nationalities() {
  const [catNation, setCatNation] = useState([]);
  const [loading, setLoading] = useState(true);

  const requestCat = () => {
    fetch('https://www.themealdb.com/api/json/v1/1/list.php?a=list')
      .then((x) => x.json()).then((y) => {
        console.log(y.meals);
        setLoading(false);
        setCatNation(y.meals);
      });
  };
  const {
    setApiSearch,
  } = useContext(recipesContext);

  const renderNation = () => (
    <select data-testid="explore-by-nationality-dropdown" name="nations">
      {
        catNation.map((x, index) => (
          <option
            data-testid={ `${x.strArea}-option` }
            key={ `${index}-option` }
            value={ x.strArea }
            onChange={ async () => {
              await fetch(`https://www.themealdb.com/api/json/v1/1/filter.php?a=${x.strArea}`)
                .then((xx) => xx.json()).then((resposta) => {
                  resposta.meals.length = 12;
                  console.log(resposta);
                  setApiSearch(resposta);
                });
            } }
          >
            { x.strArea }
          </option>
        ))
      }
    </select>
  );
  useEffect(() => {
    console.log(renderNation());
    console.log(setApiSearch);
    console.log(catNation);
    requestCat();
  }, []);
  return (
    <div>
      <Header />
      {
        loading ? null : renderNation()
      }
      <ElementCardFoods />
    </div>
  );
}

export default Nationalities;
