import React, { useContext, useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import recipesContext from '../context/recipesContext';

function ElementCard() {
  const [catFoods, setCatFoods] = useState([]);
  const [catDrinks, setCatDrinks] = useState([]);
  const [loading, setLoading] = useState(true);

  const requestCat = () => {
    fetch('https://www.themealdb.com/api/json/v1/1/list.php?c=list')
      .then((x) => x.json()).then((y) => {
        console.log(y.meals);
        setLoading(false);
        setCatFoods(y.meals);
      });
    fetch('https://www.thecocktaildb.com/api/json/v1/1/list.php?c=list')
      .then((x) => x.json()).then((y) => {
        console.log(y.drinks);
        setLoading(false);
        setCatFoods(y.drinks);
      });
  };
  const {
    apiSearch,
    setApiSearch,
  } = useContext(recipesContext);
  const history = useHistory();
  if (Object.keys(apiSearch).length === 0) {
    if (history.location.pathname === '/foods') {
      fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=')
        .then((x) => x.json()).then((y) => {
          y.meals.length = 12;
          setApiSearch(y);
        });
    } else if (history.location.pathname === '/drinks') {
      fetch('https://www.thecocktaildb.com/api/json/v1/1/search.php?s=')
        .then((x) => x.json()).then((y) => {
          y.drinks.length = 12;
          console.log(y);
          setApiSearch(y);
        });
    }
  }

  useEffect(() => {
    requestCat();
  }, []);

  const renderCards = () => {
    if (history.location.pathname === '/foods') {
      return apiSearch.meals.map((card, index) => (
        <div
          data-testid={ `${index}-recipe-card` }
          key={ index }
          className="cards"
        >
          <h1 data-testid={ `${index}-card-name` }>{ card.strMeal }</h1>
          <img
            alt="food"
            src={ card.strMealThumb }
            width="100px"
            height="100px"
            data-testid={ `${index}-card-img` }
          />
        </div>
      ));
    } if (history.location.pathname === '/drinks') {
      return apiSearch.drinks.map((card, index) => (
        <div
          data-testid={ `${index}-recipe-card` }
          key={ index }
          className="cards"
        >
          <h1 data-testid={ `${index}-card-name` }>{ card.strDrink }</h1>
          <img
            alt="food"
            src={ card.strDrinkThumb }
            width="100px"
            height="100px"
            data-testid={ `${index}-card-img` }
          />
        </div>
      ));
    }
  };

  const renderCategorias = async () => {
    if (history.pathname.location('/foods')) {
      console.log(catDrinks);
      console.log(catFoods);
      console.log(setCatDrinks);
      console.log(setCatFoods);
    }
  };

  return (
    <div
      className="elements-cards"
    >
      <div className="category">
        { loading ? null : renderCategorias() }
      </div>
      {Object.keys(apiSearch).length > 0 ? <div>{ renderCards() }</div> : null}
    </div>
  );
}

export default ElementCard;
