import React from 'react';
import PropTypes from 'prop-types';
import { useHistory } from 'react-router-dom';

function Ingredients({ element }) {
  if (localStorage.getItem('inProgressRecipes') === null) {
    localStorage.setItem('inPorgressRecipes', {
      cocktails: {
      },
      meals: {
      },
    });
  }
  const history = useHistory();
  const handleCLick = ({ target: { value } }) => {
    const atualProgress = localStorage.getItem('inProgressRecipes');
    console.log(atualProgress);
    console.log(value);
  };
  const foodIngredient = Object.values(Object
    .fromEntries(Object
      .entries(element)
      .filter(([key]) => key.includes('strIngredient'))))
    .filter((x) => x !== null && x !== '');
  const foodMeasure = Object.values(Object
    .fromEntries(Object
      .entries(element)
      .filter(([key]) => key.includes('strMeasure'))))
    .filter((x) => x !== null && x !== '');
  const ing = '-ingredient-name-and-measure';
  if (history.location.pathname.includes('/in-progress')) {
    return foodIngredient
      .map((x, index) => (
        <div
          key={ `${index}-food-ingrediet` }
          data-testid={ `${index}-ingredient-step` }
        >
          <input
            type="checkbox"
            key={ `${index}-food` }
            name={ `${index}-checkbox` }
            onClick={ handleCLick }
            value={ x }
          />
          <label htmlFor={ `${index}-checkbox` }>{ x }</label>
        </div>
      ));
  }
  return foodIngredient
    .map((x, index) => (
      <div key={ `${index}-food-ingrediet` }>
        <li key={ `${index}-food` } data-testid={ `${index}${ing}` }>{x}</li>
        <li
          key={ `${index}-` }
          data-testid={ `${index}${ing}` }
        >
          {foodMeasure[index]}
        </li>
      </div>
    ));
}

export default Ingredients;

Ingredients.propTypes = {
  element: PropTypes.shape({
    element: PropTypes.string.isRequired,
  }).isRequired,
};
