import React, { useEffect, useState } from 'react';

function FoodDetails() {
  const [res, setResponse] = useState([]);
  const requestWithId = () => {
    const href = document.location.href.split('/');
    const idFood = href[href.length - 1];
    fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${idFood}`)
      .then((x) => x.json()).then((y) => setResponse(y));
  };
  useEffect(() => {
    requestWithId();
  }, []);
  function renderIngredients() {
    const food = res.meals[0];
    let foodIngredient = Object
      .fromEntries(Object.entries(food).filter(([key]) => key.includes('strIngredient')));
    console.log(ob);
    foodIngredient = foodIngredient.filter((x) => x.includes('strIngredient'));
    return foodIngredient.map((x) => (<li key={ x }>{x}</li>));
  }
  const renderDetails = () => {
    const food = res.meals[0];
    console.log(food);
    return (
      <div>
        <img
          src={ food.strMealThumb }
          alt={ food.meal }
          width="100px"
          height="100px"
          data-testid="recipe-photo"
        />
        <h1 data-testid="recipe-title">{ food.strMeal }</h1>
        <button type="button" data-testid="share-btn">share</button>
        <button type="button" data-testid="favorite-btn">favorite</button>
        <p data-testid="recipe-category">{ food.strCategory }</p>
        <ul>
          {renderIngredients()}
        </ul>
      </div>
    );
  };
  return (
    <div className="details">
      {
        res.length === 0 ? null : renderDetails()
      }
    </div>
  );
}

export default FoodDetails;
