import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { useHistory } from 'react-router-dom';
import CheckBoxIngredient from './CheckBoxIngredient';

function Ingredients({ element }) {
  /* const [check, setCheck] = useState(false);
  const verifyCheck = (value) => {
    let { cocktails, meals } = JSON.parse(localStorage.getItem('inProgressRecipes'));
    cocktails = Object.values(cocktails);
    meals = Object.values(meals);
    console.log(meals);
    if (meals.some((x) => x === value) || cocktails.some((x) => x === value)) {
      setCheck(true);
    } else {
      setCheck(false);
    }
  }; */
  const history = useHistory();
  const handleCLick = ({ target }) => {
    const { value } = target;
    const { cocktails, meals } = JSON.parse(localStorage.getItem('inProgressRecipes'));
    let array = [value];
    if (cocktails[element.idMeal] !== undefined) {
      array = cocktails[element.idMeal];
      array.push(value);
    }
    if (history.location.pathname.includes('drinks')) {
      const newObject = {
        cocktails: {
          ...cocktails,
          [element.idDrink]: array,
        },
        meals: {
          ...meals,
        },
      };
      localStorage.setItem('inProgressRecipes', JSON.stringify(newObject));
    } else {
      const newObject = {
        cocktails: {
          ...cocktails,
        },
        meals: {
          ...meals,
          [element.idMeal]: array,
        },
      };
      localStorage.setItem('inProgressRecipes', JSON.stringify(newObject));
      target.checked = true;
    }
  };
  const foodIngredient = Object.values(Object
    .fromEntries(Object
      .entries(element)
      .filter(([key]) => key.includes('strIngredient'))))
    .filter((x) => x !== null && x !== '');
  const foodMeasure = Object.values(Object
    .fromEntries(Object
      .entries(element)
      .filter(([key]) => key.includes('strMeasure'))))
    .filter((x) => x !== null && x !== '');
  const ing = '-ingredient-name-and-measure';
  const renderIngredients = () => {
    if (history.location.pathname.includes('/in-progress')) {
      return (<CheckBoxIngredient click={ handleCLick } ingr={ foodIngredient } />);
    }
    return foodIngredient
      .map((x, index) => (
        <div key={ `${index}-food-ingrediet` }>
          <li key={ `${index}-food` } data-testid={ `${index}${ing}` }>{x}</li>
          <li
            key={ `${index}-` }
            data-testid={ `${index}${ing}` }
          >
            {foodMeasure[index]}
          </li>
        </div>
      ));
  };
  useEffect(() => {
    renderIngredients();
  }, []);
  return renderIngredients();
}

export default Ingredients;

Ingredients.propTypes = {
  element: PropTypes.shape({
    element: PropTypes.string.isRequired,
  }).isRequired,
};
