import React from 'react';

function CheckBoxIngredient({ ingr, click, i }) {
  const [check, setCheck] = useState(false);
  const verifyCheck = (value) => {
    let { cocktails, meals } = JSON.parse(localStorage.getItem('inProgressRecipes'));
    cocktails = Object.values(cocktails);
    meals = Object.values(meals);
    console.log(meals);
    if (meals.some((x) => x === value) || cocktails.some((x) => x === value)) {
      setCheck(true);
    } else {
      setCheck(false);
    }
  };
  return (
    <div
      key={ `${i}-food-ingrediet` }
      data-testid={ `${i}-ingredient-step` }
    >
      <input
        type="checkbox"
        key={ `${i}-food` }
        name={ `${i}-checkbox` }
        className="checkboxes"
        onClick={ click }
        checked={ check }
        value={ ingr }
      />
      <label htmlFor={ `${i}-checkbox` }>{ ingr }</label>
    </div>
  );
}
export default CheckBoxIngredient;
