import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';

function Recomended() {
  const history = useHistory();
  const [response, setResponse] = useState([]);
  const request = () => {
    if (history.location.pathname.includes('foods')) {
      fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=')
        .then((x) => x.json()).then((y) => {
          const { meals } = y;
          meals.length = 6;
          setResponse(meals.map((x) => ({
            name: x.strMeal,
            src: x.strMealThumb,
          })));
        });
    } else {
      fetch('https://www.thecocktaildb.com/api/json/v1/1/search.php?s=')
        .then((x) => x.json()).then((y) => setResponse(y.drinks));
    }
  };
  useEffect(() => {
    request();
  }, []);

  const renderRecomended = () => (
    <div className="recomended-cards">
      {
        response.map((x, index) => (
          <div key={ x.name } data-testid={ `${index}-recomendetion-card` }>
            <img src={ x.src } alt={ x.name } />
            <h1>{ x.name }</h1>
          </div>
        ))
      }
    </div>
  );
  return (
    <div className="recomended">
      {
        response.length === 0 ? null : renderRecomended()
      }
    </div>
  );
}

export default Recomended;
