import React, { useContext, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import recipesContext from '../context/recipesContext';

function ElementCard() {
  const {
    apiSearch,
    setApiSearch,
  } = useContext(recipesContext);
  const history = useHistory();
  if (Object.keys(apiSearch).length === 0) {
    if (history.location.pathname === '/foods') {
      fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=')
        .then((x) => x.json()).then((y) => setApiSearch(y));
    } else if (history.location.pathname === '/drinks') {
      fetch('https://www.thecocktaildb.com/api/json/v1/1/search.php?s=')
        .then((x) => x.json()).then((y) => setApiSearch(y));
    }
  }

  useEffect(() => {
    console.log(apiSearch);
  }, [apiSearch]);

  const renderCards = () => {
    if (history.location.pathname === '/foods') {
      return apiSearch.meals.map((card, index) => (
        <div
          data-testid={ `${index}-recipe-card` }
          key={ index }
          className="cards"
        >
          <h1>{ card.strMeal }</h1>
          <img alt="food" src={ card.strMealThumb } width="100px" height="100px" />
        </div>
      ));
    } if (history.location.pathname === '/drinks') {
      return apiSearch.drinks.map((card, index) => (
        <div
          data-testid={ `${index}-recipe-card` }
          key={ index }
          className="cards"
        >
          <h1>{ card.strDrink }</h1>
          <img alt="food" src={ card.strDrinkThumb } width="100px" height="100px" />
        </div>
      ));
    }
  };

  return (
    <div
      className="elements-cards"
    >
      {Object.keys(apiSearch).length > 0 ? <div>{ renderCards() }</div> : null}
    </div>
  );
}

export default ElementCard;
