import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import Recomended from '../components/Recomended';
import blackHeart from '../images/blackHeartIcon.svg';
import whiteHeart from '../images/whiteHeartIcon.svg';
import Ingredients from '../components/Ingredients';
import saveRecipe from '../helpers/saveRecipe';

function DrinkDetails() {
  const history = useHistory();
  fetch('https://www.thecocktaildb.com/api/json/v1/1/search.php?s=');
  fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=');
  const [res, setResponse] = useState([]);
  const [area, setArea] = useState('');
  const [srcImg, setSrcImg] = useState(whiteHeart);
  function verifyFav(id) {
    console.log(id);
    let favs = JSON.parse(localStorage.getItem('favoriteRecipes'));
    if (favs !== null) {
      favs = Object.values(favs);
      if (favs.some((x) => x.id === id)) {
        setSrcImg(blackHeart);
      }
      console.log('lint');
    }
  }
  function requestWithId() {
    const href = document.location.href.split('/');
    const idFood = href[href.length - 1];
    setArea('');
    fetch(`https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${idFood}`)
      .then((x) => x.json()).then((y) => {
        setResponse(y);
        console.log(y.drinks[0].strDrink);
      }).then(() => {
        verifyFav(idFood);
      });
  }
  useEffect(() => {
    requestWithId();
  }, []);
  const renderDetails = () => {
    const drink = res.drinks[0];
    return (
      <div>
        <img
          src={ drink.strDrinkThumb }
          alt={ drink.drink }
          width="100px"
          height="100px"
          data-testid="recipe-photo"
        />
        <h1 data-testid="recipe-title">{ drink.strDrink }</h1>
        <button
          type="button"
          data-testid="share-btn"
          className="share-btn"
          onClick={ () => {
            navigator.clipboard.writeText(`http://localhost:3000${history.location.pathname}`);
            const btn = document.querySelector('.share-btn');
            btn.innerHTML = 'Link copied!';
          } }
        >
          share
        </button>
        <button
          type="button"
          data-testid="favorite-btn"
          src={ srcImg }
          onClick={ () => {
            console.log(area);
            const favObject = [{
              id: drink.idDrink,
              type: 'bebida',
              nationality: drink.strArea,
              category: '',
              alcoholicOrNot: '',
              name: '',
              image: '',
            }];
            saveRecipe(favObject);
            if (srcImg === blackHeart) {
              setSrcImg(whiteHeart);
            } else {
              setSrcImg(blackHeart);
            }
          } }
        >
          <img src={ srcImg } alt="fav" />
        </button>
        <p data-testid="recipe-category">{ drink.strAlcoholic }</p>
        <ul>
          <Ingredients element={ res.drinks[0] } />
        </ul>
        <p data-testid="instructions">{ drink.strInstructions }</p>
        <video width="320" height="240" data-testid="video" controls>
          <track kind="captions" />
          <source src={ drink.strSource } type="video/mp4" />
        </video>
        <Recomended />
        <button
          data-testid="start-recipe-btn"
          type="button"
          className="start-button"
          onClick={ () => {
            history.push(`/drinks/${drink.idDrink}/in-progress`);
          } }
        >
          iniciar
        </button>
      </div>
    );
  };
  return (
    <div className="details">
      {
        res.length === 0 ? null : renderDetails()
      }
    </div>
  );
}

export default DrinkDetails;
