import React, { useContext, useEffect, useState } from 'react';
import recipesContext from '../context/recipesContext';

function ElementCardFoods() {
  const [catDrinks, setCatDrinks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [toggle, setToggle] = useState('');

  const requestCat = () => {
    fetch('https://www.themealdb.com/api/json/v1/1/list.php?c=list')
      .then((x) => x.json()).then((y) => {
        console.log(y.meals);
        y.meals.length = 5;
        setLoading(false);
        setCatDrinks(y.meals);
      });
  };
  const {
    apiSearch,
    setApiSearch,
  } = useContext(recipesContext);
  if (Object.keys(apiSearch).length === 0) {
    fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=')
      .then((x) => x.json()).then((y) => {
        y.meals.length = 12;
        console.log(y);
        setApiSearch(y);
      });
  }

  useEffect(() => {
    requestCat();
  }, []);
  const renderCards = () => apiSearch.meals.map((card, index) => (
    <button
      data-testid={ `${index}-recipe-card` }
      key={ index }
      type="button"
      className="cards"
      onClick={ () => { history.push(`/foods/${card.idMeal}`); } }
    >
      <h1 data-testid={ `${index}-card-name` }>{ card.strMeal }</h1>
      <img
        alt="food"
        src={ card.strMealThumb }
        width="100px"
        height="100px"
        data-testid={ `${index}-card-img` }
      />
    </button>
  ));

  const handleCLick = ({ target: { value } }) => {
    if (toggle === value) {
      setApiSearch([]);
      setToggle('');
    } else {
      fetch(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${value}`)
        .then((q) => q.json()).then((p) => {
          console.log(p);
          p.meals.length = 12;
          setApiSearch(p);
          setToggle(value);
        });
    }
  };
  const renderCategorias = () => (
    catDrinks.map((y, index) => (
      <button
        type="button"
        key={ `cat${index}` }
        value={ y.strCategory }
        className="cat-card"
        data-testid={ `${y.strCategory}-category-filter` }
        onClick={ handleCLick }
      >
        {
          y.strCategory
        }
      </button>))
  );
  return (
    <div
      className="elements-cards"
    >
      <div className="category">
        { loading ? null : renderCategorias() }
        <button
          data-testid="All-category-filter"
          onClick={ () => setApiSearch([]) }
          type="button"
        >
          All
        </button>
      </div>
      {Object.keys(apiSearch).length > 0 ? <div>{ renderCards() }</div> : null}
    </div>
  );
}
export default ElementCardFoods;
